import React, { useState } from 'react';

const UrlForm = () => {
  const [originalUrl, setOriginalUrl] = useState('');
  const [shortUrl, setShortUrl] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();

    fetch(`${process.env.REACT_APP_API_BASE_URL}/shorten`, {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ originalUrl: originalUrl })
      })
      .then(res => res.text())
      .then(data => {
        console.log("Short URL:", data);
        setShortUrl(data);
      });
      
    // const short = await res.text();
    // console.log(short, "short url")
    // setShortUrl(short);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input value={originalUrl} onChange={(e) => setOriginalUrl(e.target.value)} placeholder="Enter URL" />
        <button type="submit">Shorten</button>
      </form>
      {shortUrl && <p>Short URL: <a href={shortUrl}>{shortUrl}</a></p>}
    </div>
  );
};

export default UrlForm;
