import React, { useState } from 'react';

const UrlForm = () => {
    const [originalUrl, setOriginalUrl] = useState('');
    const [shortUrl, setShortUrl] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        const res = await fetch(`${process.env.REACT_APP_API_BASE_URL}/shorten`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ originalUrl: originalUrl }) // or your field name
        });

        const short = await res.text();
        console.log(short, "short url")
        setShortUrl(short);
    };

    const handleRedirect = async (shortUrl) => {
        try {
          const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/${shortUrl}`, {
            method: "GET",
            redirect: "manual", // Keep manual if you want to handle yourself
          });
      
          if (response.status === 302) {
            const location = response.headers.get("Location");
            window.location.href = location;
          } else if (response.status === 410) {
            alert("This URL has expired.");
          } else if (response.status === 404) {
            alert("URL not found.");
            window.location.href = "http://localhost:3000"; // Or your page
          }
        } catch (err) {
          alert("Server error.");
        }
      };
      


    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input value={originalUrl} onChange={(e) => setOriginalUrl(e.target.value)} placeholder="Enter URL" />
                <button type="submit">Shorten</button>
            </form>

            {shortUrl && (
                <p>
                Short URL:{" "}
                <button onClick={() => handleRedirect(shortUrl)} style={{ color: "blue", textDecoration: "underline", background: "none", border: "none" }}>
                  {shortUrl}
                </button>
              </p>
              
            )}
            {/* {shortUrl && (
                <p>
                    Short URL:{" "}
                    <a
                        href={shortUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        {shortUrl}
                    </a>
                </p>
            )} */}

            {/* {shortUrl && (
  <p>
    Short URL: <a href={shortUrl} target="_blank" rel="noopener noreferrer">{shortUrl}</a>
  </p>
)} */}
        </div>
    );
};

export default UrlForm;
