import React, { useState } from 'react';

const UrlForm = () => {
    const [originalUrl, setOriginalUrl] = useState('');
    const [shortUrl, setShortUrl] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();

        const res = await fetch(`${process.env.REACT_APP_API_BASE_URL}/shorten`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ originalUrl: originalUrl }) // or your field name
        });

        const short = await res.text();
        console.log(short, "short url")
        setShortUrl(short);
    };

    const handleRedirect = async (shortCode) => {
        try {
            const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/${shortCode}`, {
                method: "GET",
                redirect: "manual",
            });

            if (response.status === 302) {
                const redirectUrl = response.headers.get("Location");
                window.location.href = redirectUrl;
            } else if (response.status === 410) {
                alert("This URL has expired.");
                window.location.href = "http://localhost:3000/"; // Redirect to home page to create a new one
            } else if (response.status === 404) {
                alert("URL not found.");
                window.location.href = "http://localhost:3000/"; // Redirect to home page to create a new one
            }
        } catch (err) {
            alert("Server error.");
        }
    };


    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input value={originalUrl} onChange={(e) => setOriginalUrl(e.target.value)} placeholder="Enter URL" />
                <button type="submit">Shorten</button>
            </form>

            {shortUrl && (
                <p>
                    Short URL:{" "}
                    <a
                        href="#"
                        onClick={(e) => {
                            e.preventDefault();
                            handleRedirect(shortUrl.split("/").pop());
                        }}
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        {shortUrl}
                    </a>
                </p>
            )}
            {/* {shortUrl && (
                <p>
                    Short URL:{" "}
                    <a
                        href={shortUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                    >
                        {shortUrl}
                    </a>
                </p>
            )} */}

            {/* {shortUrl && (
  <p>
    Short URL: <a href={shortUrl} target="_blank" rel="noopener noreferrer">{shortUrl}</a>
  </p>
)} */}
        </div>
    );
};

export default UrlForm;
