import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const UrlForm = () => {
    const [originalUrl, setOriginalUrl] = useState('');
    const [shortUrl, setShortUrl] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        const res = await fetch(`${process.env.REACT_APP_API_BASE_URL}/shorten`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ originalUrl: originalUrl }) // or your field name
        });

        const short = await res.text();
        console.log(short, "short url")
        setShortUrl(short);
    };

    const handleRedirect = async (shortUrl) => {
        try {
            const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}`,shortUrl, {
                method: "GET",
            });

            console.log(response, "reeeeeeeeeeeeeeeeeeeeeeeee")
    
            if (response.status === 302) {
                const location = response.headers.get("Location");
                if (location) {
                    window.location.href = location;
                } else {
                    alert("Redirect location not found.");
                }
            } else if (response.status === 410) {
                alert("This link has expired. Please create a new one.");
            } else if (response.status === 404) {
                alert("This short URL does not exist. Redirecting to URL creation page...");
                navigate("/"); // assuming your URL creation page is at `/`
            } else {
                alert("Unexpected error. Please try again.");
            }
        } catch (err) {
            console.error("Fetch error:", err);
            alert("An error occurred while processing the short URL.");
        }
    };
    

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <input value={originalUrl} onChange={(e) => setOriginalUrl(e.target.value)} placeholder="Enter URL" />
                <button type="submit">Shorten</button>
            </form>
            {shortUrl && (
                <p>
    Short URL:{" "}
    <button onClick={() => handleRedirect(shortUrl)} style={{ cursor: "pointer", color: "blue", textDecoration: "underline", background: "none", border: "none" }}>
        {shortUrl}
    </button>
</p>                // <p>
                //     Short URL: <a href={shortUrl} target="_blank" rel="noopener noreferrer">{shortUrl}</a>
                // </p>
            )}
        </div>
    );
};

export default UrlForm;
