import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const UrlForm = () => {
  const [originalUrl, setOriginalUrl] = useState('');
  const [shortUrl, setShortUrl] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    const res = await fetch(`${process.env.REACT_APP_API_BASE_URL}/shorten`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ originalUrl: originalUrl }) // or your field name
      });

    const short = await res.text();
    console.log(short, "short url")
    setShortUrl(short);
  };

  const handleTestRedirect = () => {
    navigate(shortUrl);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input value={originalUrl} onChange={(e) => setOriginalUrl(e.target.value)} placeholder="Enter URL" />
        <button type="submit">Shorten</button>
      </form>
      {shortUrl && (
        <button onClick={handleTestRedirect}>Go to Short URL</button>
//   <p>
//     Short URL: <a href={shortUrl} target="_blank" rel="noopener noreferrer">{shortUrl}</a>
//   </p>
)}
    </div>
  );
};

export default UrlForm;
